CREATE TEMP TABLE _temp_modular_items
(
    existing_item_definition_id TEXT NOT NULL,
    new_item_definition_id TEXT NOT NULL,
    pseudo_id INT NOT NULL,
    parent_pseudo_id INT,
    component_id INT
);


-- Create a temporary table to hold data for the modular weapon items
CREATE TEMP TABLE _temp_modular_component_items
(
    temp_item_id INTEGER PRIMARY KEY AUTOINCREMENT  NOT NULL, -- new id, increment from 1
    base_item_id INTEGER NOT NULL,
    new_component_item_id INTEGER NULL,
    new_item_definition_id TEXT NOT NULL,
    existing_item_definition_id TEXT NOT NULL,
    pseudo_id INT NOT NULL,
    parent_pseudo_id INT,
    component_id INT
);

SELECT  NULL, --temp_item_id, autoincrement
        i.item_id, --base_item_id
        NULL, --new_component_item_id, updated in query below
        m.new_item_definition_id,
        m.existing_item_definition_id,
        m.pseudo_id,
        m.parent_pseudo_id,
        m.component_id
FROM    _temp_modular_items m
JOIN    item i ON (m.existing_item_definition_id = item_definition_id)
ORDER BY i.item_id;

-- Update the item_definition_id for the modular base items
UPDATE  item
SET     item_definition_id = (SELECT new_item_definition_id from _temp_modular_items m WHERE m.existing_item_definition_id = item_definition_id AND m.parent_pseudo_id IS NULL)
WHERE   EXISTS (SELECT new_item_definition_id FROM _temp_modular_items m WHERE m.existing_item_definition_id = item_definition_id AND m.parent_pseudo_id IS NULL);

-- Create an item_id for each new modular component item
INSERT
INTO    entity
SELECT  NULL
FROM    _temp_modular_component_items;

-- Assign a new entity_id to each modular component item
UPDATE  _temp_modular_component_items
SET     new_component_item_id = ((SELECT MAX(entity_id) FROM entity) + 1 - temp_item_id)
WHERE   parent_pseudo_id IS NOT NULL;

-- Insert the new modular component items, temporarily disabling foreign key constraints
-- due to the parent_container_item_id foreign key constraint not being satisfied
-- until the end of the query.
PRAGMA defer_foreign_keys = true;

CREATE INDEX idx_temp_modular_component_items_parent_pseudo_id ON _temp_modular_component_items (parent_pseudo_id);
CREATE INDEX idx_temp_modular_component_items_pseudo_id ON _temp_modular_component_items (pseudo_id);
CREATE INDEX idx_temp_modular_component_items_base_item_id ON _temp_modular_component_items (base_item_id);

WITH RECURSIVE item_graph(item_id, new_component_item_id, parent_container_item_id, new_item_definition_id, pseudo_id, parent_pseudo_id, base_item_id, component_id) AS
(
    SELECT i.item_id, i.item_id, i.parent_container_item_id, t.new_item_definition_id, t.pseudo_id, parent_pseudo_id, t.base_item_id, t.component_id
    FROM    _temp_modular_component_items t
    JOIN   item i ON (i.item_id = t.base_item_id)
    WHERE t.parent_pseudo_id IS NULL

    UNION ALL

    SELECT item_graph.item_id, t.new_component_item_id, item_graph.new_component_item_id, t.new_item_definition_id, t.pseudo_id, t.parent_pseudo_id, t.base_item_id, t.component_id
    FROM    _temp_modular_component_items t
    JOIN item_graph ON t.parent_pseudo_id = item_graph.pseudo_id AND t.base_item_id = item_graph.base_item_id
)
SELECT  new_component_item_id,
        parent_container_item_id,
        new_item_definition_id,
        1, --stack_size
        'component_' || component_id --position
FROM    item_graph
WHERE   parent_pseudo_id IS NOT NULL;

PRAGMA defer_foreign_keys = false;
